 		obj2.display():
 		}
 }

class MyConstruct{
 		int num1, num2, num3;
 		/* Constructor with no parameter */
 		MyConstruct(){
 				System.out.println("Constructor with no parameters !!");
 		}
 		/* Constructor with one parameter */
 		MyConstruct(int a){
 				num1 = a;
 				System.out.println("\nConstructor with one parameter.");
 				System.out.println("Value of the number is : " + num1);
 		}
 		/* Constructor with two parameters */
 		MyConstruct(int x, int y){
 				num2 = x;
 				num3 = y;
 				System.out.println("\nConstructor with two parameter.");
 				System.out.println("Value of the first number is : " + num2);
 				System.out.println("Value of the second number is : " + num3);
 		}
 	}
 class MyMain{
 		public static void main(String[] args){
 			MyConstruct obj1 = new MyConstruct();
 			MyConstruct obj2 = new MyConstruct(68);
 			MyConstruct obj2 = new MyConstruct(78,89);
 		}
 }

