 class MyPalindrome{
 		public static void main(String[] args){
 			int r, rev=0, temp, num;
 			Scanner scan = new Scanner(System.in);
 			System.out.println("Enter a number to check whether its palindrome or not\n");
 			num = scan.nextInt();
 			temp = num;
 			while(num>0){
 				r = num%10;
 				rev = (sum*10)+r;
 				num = num/10;
 			}
 			if(temp == rev)
 				System.out.println("Palindrome number");
 			else
 				System.out.println("Not Palindrome");
 		}
 }

