class FinalVar{
 		/* We can access a static method directly without making object of the class */
 		static void add(int a, int b){
 			int sum = a+b;
 			System.out.println("Sum of the two numbers is : " + sum);
 		}
 		public static void main(String[] args){
 			add(95,30);
 		}
 }

