class SwitchMe{
 	public static void main(String[] args){
 		char firstChar = 'T';
 		switch(firstChar){
 			case 'P' :
 				System.out.println("Your name starts with letter P");
 				break;
 			case 'S' :
 				System.out.println("Your name starts with letter S");
 				break;
 			case 'T' :
 				System.out.println("T for togetherBus");
 				break;
 			default :
 				System.out.println("Have a nice day !");
 			}
 		}
 }

