class ThrowsEx{
 		static void check() throws ArithmeticException{
 			System.out.println("Inside check function");
 			throw new ArithmeticException("demo");
 		}
 		public static void main(String[] args){				
 			try{
 				check();
 			}catch(ArithmeticException e){
 				System.out.println("caught " + e);
 			}
 		}
 	}

type method_name(parameter_list) throws exception_list\n{\n
\t\t // definition of method\n
}

class Except{
 		public static void main(String[] args){				
 			int a,b,c;
 			try{
 				a = 0;
 				b = 20;
 				c = b/a;
 				System.out.println("This line will not be executed");
 			}catch(ArithmeticException e){
 				System.out.println("Please do not divide a number by zero !!");
 			}
 			System.out.println("This will execute after the exception is handled");
 			}
 	}

class UncaughtException{
 		public static void main(String[] args){				
 			int x = 0;
 			int y = 5/x; //dividing by zero will lead to exception
 		}
 }

import java.util.Scanner;
 class MyCalc{
 	public static void main(String[] args){
 		Scanner scan = new Scanner(System.in);
 		int num1, num2, sum;
 		System.out.println("-: Program to add two numbers :-\n");
 		System.out.println("Enter the first number : ");
 		/* Taking first input from user */
 		num1 = scan.nextInt();
 		System.out.println("Enter the second number : ");
 		/* Taking second input from user */
 		num2 = scan.nextInt();
 		sum = num1 + num2;
 		System.out.println("Result of addition : " + sum);
 		}
 	}

