class Vehicle{
 		String regno;
 		int model;
 		Vehicle(String r, int m){
 			regno = r;
 			model = m;
 		}
 		void display(){
 			System.out.println("Registration number : " + regno);
 			System.out.println("Model number : " + model);
 		}
 }
 class Bike extends Vehicle{
 		int wheels;
 		Bike(String r, int m, int n){
 			super(r,m);
 			wheels = n;
 		}
 		void display(){
 			System.out.println("-: Bike :-");
 			super.display();
 			System.out.println("Number of wheels : " + wheels);
 			System.out.println();
 		}
 }
 class AutoRickshaw extends Vehicle{
 		int wheels;
 		AutoRickshaw(String r, int m, int n){
 			super(r,m);
 			wheels = n;
 		}
 		void display(){
 			System.out.println("-: AutoRickshaw :-");
 			super.display();
 			System.out.println("Number of wheels : " + wheels);
 			System.out.println();
 		}
 }
 class Car extends Vehicle{
 		int wheels;
 		Car(String r, int m, int n){
 			super(r,m);
 			wheels = n;
 		}
 		void display(){
 			System.out.println("-: Car :-");
 			super.display();
 			System.out.println("Number of wheels : " + wheels);
 			System.out.println();
 		}
 }
 class MyMain{
 		public static void main(String[] args){
 			Bike obj1 = new Bike("JG4HG",6,2);
 			AutoRickshaw obj2 = new AutoRickshaw("NF4NF",5,3);
 			Car obj3 = new Car("OND3P",9,4);
 			obj1.display();
 			obj2.display();
 			obj3.display();
 		}
 }

